import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Button, View, SafeAreaView, Text, Alert, AppRegistry } from 'react-native';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/database';


var config = {
  credential: "TIiJOvAjbhyd4NdCAmaAMBUOy91CDsnytsGCEoQU",
  databaseURL: "https://smart-water-management-s-887ee-default-rtdb.firebaseio.com/",
  projectId: "smart-water-management-s-887ee",
};

firebase.initializeApp(config);
var tog = 1;
export default function App() {

  return (
    <View>
      <View style={styles.headerContainer}>
        <Text>Smart Water Management</Text>
      </View>
      <View style={styles.container}>
        <View style={styles.textContainer}>
          <Text>Manual Mode</Text>
        </View>
        <View style={styles.buttonContainer}>
          <Button onPress={
            () => firebase.database().ref('WatrManage/').update({
              man: tog
            }).then((data)=>{
              //success callback
              console.log('data ' , data)
            }).catch((error)=>{
              //error callback
              console.log('error ' ,error)
            })
          } title="on/off" color="#2E9298" accessibilityLabel="Tap on Me"/>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerContainer: {
    padding: '50px',
    alignItems: 'center',
    paddingTop: '15px',
    backgroundColor: '#1abc9c',
    color: 'white',
    fontSize: '90px',
  },
  textContainer: {
    color: 'grey',
  },
  buttonContainer: {
    backgroundColor: '#2E9298',
    borderRadius: 10,
    padding: 10,
    shadowColor: '#000000',
    shadowOffset: {
      width: 0,
      height: 3
    },
    shadowRadius: 10,
    shadowOpacity: 0.25
  }
});
