import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Button, View, SafeAreaView, Text, Alert, AppRegistry } from 'react-native';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/database';
import { createAppContainer } from "react-navigation";
import { createStackNavigator } from "react-navigation-stack";


var config = {
  credential: "TIiJOvAjbhyd4NdCAmaAMBUOy91CDsnytsGCEoQU",
  databaseURL: "https://smart-water-management-s-887ee-default-rtdb.firebaseio.com/",
  projectId: "smart-water-management-s-887ee",
};

firebase.initializeApp(config);

const AppNavigator = createStackNavigator(
  {
    Home: HomeScreen,
    User: UserScreen,
    Setting: SettingScreen,
  },
  {
    defaultNavigationOptions: {
      headerStyle: {
        backgroundColor: "#006600",
      },
      headerTitleStyle: {
        fontWeight: "bold",
        color: "#FFF",
      },
      headerTintColor: "#FFF",
    },
  },
  {
    initialRouteName: "Home",
  }
);

const Navigator = createAppContainer(AppNavigator);

export default function App() {

  return (

    <View style={styles.container}>
      <Navigator>
      </Navigator>
        <View style={styles.textContainer}>
          Manual Mod
        </View>
        <View style={styles.buttonContainer}>
          <Button onPress={
            () => firebase.database().ref('WatrManage/').set({
                    level: 3,
                    man: 1,
                    motorstatus: 1
                  }).then((data)=>{
                    //success callback
                    console.log('data ' , data)
                  }).catch((error)=>{
                    //error callback
                    console.log('error ' ,error)
                  })
          } title="on/off" color="#2E9298" accessibilityLabel="Tap on Me"/>
        </View>
      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textContainer: {

  },
  buttonContainer: {
    backgroundColor: '#2E9298',
    borderRadius: 10,
    padding: 10,
    shadowColor: '#000000',
    shadowOffset: {
      width: 0,
      height: 3
    },
    shadowRadius: 10,
    shadowOpacity: 0.25
  }
});
